$(window).load(function(){let c=$("#slider"),n=$("#productModal"),i=$(".add-cart-btn"),o=$(".close");c.nivoSlider({effect:"fade",pauseTime:5e3,controlNav:!1}),i.click(function(){n.css("display","block")}),o.click(function(){n.css("display","none")}),n.click(function(c){c.target==n[0]&&n.css("display","none")})});